import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SpotifyService {

  constructor(private http: HttpClient) { }

  private httpOptions = {
    headers: new HttpHeaders({
      "Accept": "application/json",
      "Content-Type": "application/json",
      "Authorization": "Bearer BQBn16dxI4PFLmI2IcBPgMH9wiK-IMnjJMOcTODO7iVnYaeQeGpnW6kX0w6ugxuW5wzamAlPg_kPeQWeLqqsQlWFswZqvQhs094QWUElYVRYOOmv_AfC"
    })
  }

  getAllArtists(search:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/search?q=${search}&type=artist`, this.httpOptions)
  }
  getArtists(id:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${id}`, this.httpOptions)
  }
  getAlbums(id:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${id}/albums`, this.httpOptions)
  }
  getTopSongs(artistId: string, country: string = 'US'): Observable<any> {
    const url = `https://api.spotify.com/v1/artists/${artistId}/top-tracks?country=${country}`;
    return this.http.get<any>(url, this.httpOptions);
  }   
  getTopTracks(artistId: string): Observable<any> {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${artistId}/top-tracks?market=US`, this.httpOptions);
  }  
}
