import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap } from '@angular/router';
import { __param } from 'tslib';
import { SpotifyService } from '../spotify.service';
import { Router } from '@angular/router';
import { forkJoin } from 'rxjs';


@Component({
  selector: 'app-artist',
  templateUrl: './artist.component.html',
  styleUrls: ['./artist.component.css']
})
export class ArtistComponent implements OnInit{
  artistID: any;
  artistInfo: Array<any> = []
  artistName = '';

  constructor(private activateRoute: ActivatedRoute, private spotifyservice: SpotifyService, private router: Router){
    activateRoute.paramMap.subscribe((__param: ParamMap) =>{
      this.artistID = __param.get('id');
    });
  }

  ngOnInit() {
    const artistId = this.activateRoute.snapshot.paramMap.get('id');
    console.log(artistId);

    if (artistId) {
      this.spotifyservice.getArtists(artistId).subscribe(data => {
        this.artistName = data.name;
      });
    }
  }  
}
