import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SpotifyService {

  constructor(private http: HttpClient) { }

  private httpOptions = {
    headers: new HttpHeaders({
      "Accept": "application/json",
      "Content-Type": "application/json",
      "Authorization": "Bearer BQCeuXsubZYg-Q10PApEjrM8oRgwOnjQro2PAs6UO3ooT-CVVimUrDChLbGLfcqBMYLq2uyTqhADYr6OYbHnUhtrx8qPhMzJAfaJ0SqQT4e49Efsrl7k"
    })
  }

  getAllArtists(search:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/search?q=${search}&type=artist`, this.httpOptions)
  }
  getArtists(id:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${id}`, this.httpOptions)
  }
  getAlbums(id:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${id}/albums`, this.httpOptions)
  }
  getTopTracks(artistId: string) {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${artistId}/top-tracks?country=US`, this.httpOptions);
  }
}
