import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { SpotifyService } from '../spotify.service';

@Component({
  selector: 'app-rankboard',
  templateUrl: './rankboard.component.html',
  styleUrls: ['./rankboard.component.css']
})
export class RankboardComponent {
  topSongs: any[] = [];

  getTopSongs() {
    const httpOptions = {
      headers: new HttpHeaders({
        'Authorization': 'Bearer YOUR_SPOTIFY_ACCESS_TOKEN'
      })
    };

    const url = 'https://api.spotify.com/v1/playlists/37i9dQZEVXbMDoHDwVN2tF/tracks?limit=100';

    this.http.get<any>(url, httpOptions).subscribe(
      (data) => {
        this.topSongs = data.items.map((item: any) => item.track);
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
