import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { SpotifyService } from '../spotify.service';

@Component({
  selector: 'app-albumcategory',
  templateUrl: './albumcategory.component.html',
  styleUrls: ['./albumcategory.component.css']
})
export class AlbumcategoryComponent implements OnInit {

  albums$: Observable<any> | null = null;
  genreId!: string;

  constructor(private spotifyService: SpotifyService, private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.genreId = params.get('id') || '';
      this.albums$ = this.spotifyService.getCategoryAlbums(this.genreId);
    });
  }
}
