import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SpotifyService {

  constructor(private http: HttpClient) { }

  private httpOptions = {
    headers: new HttpHeaders({
      "Accept": "application/json",
      "Content-Type": "application/json",
      "Authorization": "Bearer BQDaOBBkagYI3-2MELlKU-xzbpNpF7ZRFQGQ3PGPs93LKJpEqqyF8bm10qxCmKprRAuWJHNXMnsvNzvcxczs00WF59XeJZBFBm7RCUaZZ12-U3Vy2d-hpBl7CnIsth4Y-Y99wki1p0jJ4k6fZGmxL6kisYxWtuPd4iwoP6yBrf3k_Bhpz7tv4L0mHYcjNrf2uYOZ"
    })
  }

  getAllArtists(search:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/search?q=${search}&type=artist`, this.httpOptions)
  }
}
