import { Component } from '@angular/core';
import { ActivatedRoute, ParamMap } from '@angular/router';
import { __param } from 'tslib';
import { SpotifyService } from '../spotify.service';

@Component({
  selector: 'app-artist',
  templateUrl: './artist.component.html',
  styleUrls: ['./artist.component.css']
})
export class ArtistComponent {
  artistID: any;
  artist: any;
  albums: any;

  constructor(private activateRoute: ActivatedRoute, private spotifyservice: SpotifyService){
    activateRoute.paramMap.subscribe((__param: ParamMap) =>{
      this.artistID = __param.get('id');
    });
    spotifyservice.getArtists(this.artistID).subscribe(data =>{
      this.artist = data;
    });
  }
}
