import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { SpotifyService } from '../spotify.service';

@Component({
  selector: 'app-rankboard',
  templateUrl: './rankboard.component.html',
  styleUrls: ['./rankboard.component.css']
})
export class RankboardComponent {
  topSongs: any[] = [];

  constructor(private spotifyService: SpotifyService) { }

  ngOnInit(): void {
    this.getTopSongs();
  }

  getTopSongs(): void {
    // Call the Spotify API to get the top 100 songs
    this.spotifyService.getTopSongs().subscribe(
      data => {
        this.topSongs = data.tracks;
        console.log(this.topSongs);
      },
      error => {
        console.log(error);
      }
    );
  }
}
