import { Component } from '@angular/core';
import { SpotifyService } from './spotify.service';

@Component({
  selector: 'app-root',
  template: `
    <input type="text" [(ngModel)]="search" (keyup)="loadData()">
    <!-- <pre>{{artists | json}}</pre> -->
    <div *ngFor="let art of artists">
      <h3> <a routerLink="/artist/{{art.id}}">Tên tác giả: {{art.name}}</a> </h3>
      <p>Số lượng theo dõi {{art.followers.total | number}}</p>
      <ul>Thể loại nhạc 
        <li *ngFor="let tl of art.genres">
          {{tl}}
        </li>
      </ul>
      <img src="{{art?.images[0]?.url}}" width="200px">
    </div>
    <router-outlet></router-outlet>
  `
})
export class AppComponent {
  artists: Array<any> = []
  search!: string
  constructor(private spotifyService: SpotifyService) { this.loadData() }

  loadData() {
    this.spotifyService.getAllArtists(this.search).subscribe(
      data => this.artists = data.artists.items
    )
  }
}
