import { Component, OnInit } from '@angular/core';
import { SpotifyService } from '../spotify.service';

@Component({
  selector: 'app-albumcategory',
  templateUrl: './albumcategory.component.html',
  styleUrls: ['./albumcategory.component.css']
})
export class AlbumcategoryComponent implements OnInit {

  albums: any[] = [];

  constructor(private spotifyService: SpotifyService) { }

  ngOnInit(): void {
    this.spotifyService.getCategoryAlbums('rock').subscribe(
      data => {
        this.albums = data;
        console.log(this.albums);
      },
      error => {
        console.log(error);
      }
    );
  }

}
