import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

interface Album {
  id: string;
  name: string;
  artist: string;
  image: string;
}

@Injectable({
  providedIn: 'root'
})
export class SpotifyService {

  constructor(private http: HttpClient) { }

  private httpOptions = {
    headers: new HttpHeaders({
      "Accept": "application/json",
      "Content-Type": "application/json",
      "Authorization": "Bearer BQCURGYWmKUPFCxtGwPp96tcBA-qwU925oUaPyW_3IzQtpHeoYw5acHH_j3VkwfX2YCSTSs66NpEMJX3Q8T0R4XMQHTdj-1kkgiAc_sZ65DKElMSwOdo"
    })
  }

  getAllArtists(search:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/search?q=${search}&type=artist`, this.httpOptions)
  }
  getArtists(id:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${id}`, this.httpOptions)
  }
  getAlbums(id:string) {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${id}/albums`, this.httpOptions)
  }
  getTopSongs(artistId: string): Observable<any> {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${artistId}/top-tracks?country=US&limit=20&order=desc`, this.httpOptions);
  }   
  getTopTracks(artistId: string): Observable<any> {
    return this.http.get<any>(`https://api.spotify.com/v1/artists/${artistId}/top-tracks?market=US`, this.httpOptions);
  }  

  getGenres(): Observable<any> {
  return this.http.get<any>('https://api.spotify.com/v1/browse/categories', this.httpOptions)
    .pipe(map(data => data.categories.items.map((item: { id: any; name: any; icons: string | any[]; }) => {
      return { 
        id: item.id,
        name: item.name,
        image: item.icons.length > 0 ? item.icons[0].url : ''
      };
    })));
  }

  getCategoryAlbums(genreId: string): Observable<Album[]> {
    const url = `https://api.spotify.com/v1/albums/${genreId}`;
    return this.http.get<any>(url, this.httpOptions).pipe(
      map((response) =>
        response.albums.items.map((item: any) => ({
          id: item.id,
          name: item.name,
          artist: item.artists[0].name,
          image: item.images.length > 0 ? item.images[0].url : ''
        }))
      )
    );
  }

  getAlbumSongs(albumId: string): Observable<any> {
    const headers = new HttpHeaders({
      'Authorization': 'Bearer ' + localStorage.getItem('access_token')
    });
    return this.http.get(`https://api.spotify.com/v1/albums/${albumId}/tracks`, { headers })
      .pipe(
        map((data: any) => data)
      );
  }
}
